<?phpfunction is_sudoku(array $sudoku): bool  {    for($i=0; $i<count($sudoku); $i++)    {        //dummy array with 9 a values        $array = array_fill(0, 10, 'a');        for($j=0;$j<count($sudoku);$j++)        {            $x=$sudoku[$i][$j];            if($x>9|| $x<0)            {                return FALSE;            }            if($array[$x] == 'a')            {                $array[$x]=$x;            }            else            {                return FALSE;            }        }    }    for($i=0;$i<count($sudoku);$i++)    {        //dummy array with 9 a values        $array = array_fill(0, 10, 'a');        for($j=0;$j<count($sudoku);$j++)        {            $x=$sudoku[$j][$i];            if($x>9|| $x<0)            {                return FALSE;            }            if($array[$x] == 'a')            {                $array[$x]=$x;            }            else            {                return FALSE;            }        }    }    for ($i = 0; $i < count($sudoku) - 2; $i += 3) {        for ($j = 0; $j < count($sudoku) - 2; $j += 3) {            //dummy array with 9 a values            $array = array_fill(0, 10, 'a');            // Traversing the present 3*3 block            for ($k = 0; $k < 3; $k++) {                for ($l = 0; $l < 3; $l++) {                    //row number                    $X = $i + $k;                    //column numer                    $Y = $j + $l;                    //value of present row column element                    $Z = $sudoku[$X][$Y];                    if($array[$Z] == 'a')                    {                        $array[$Z]=$Z;                    }                    else                    {                        return FALSE;                    }                }            }        }    }    return TRUE;}$valid_sudokus = array(    [1, 3, 2, 5, 4, 6, 9, 8, 7],    [4, 6, 5, 8, 7, 9, 3, 2, 1],    [7, 9, 8, 2, 1, 3, 6, 5, 4],    [9, 2, 1, 4, 3, 5, 8, 7, 6],    [3, 5, 4, 7, 6, 8, 2, 1, 9],    [6, 8, 7, 1, 9, 2, 5, 4, 3],    [5, 7, 6, 9, 8, 1, 4, 3, 2],    [2, 4, 3, 6, 5, 7, 1, 9, 8],    [8, 1, 9, 3, 2, 4, 7, 6, 5],// [3, 1, 7, 4, 8, 2, 9, 6, 5]);$result=is_sudoku($valid_sudokus);echo $result;?>